      "source": [
        "# standardize data within each event\n",
        "df_avg <- df_avg |>\n",
        "  dplyr::group_by(eventId) |>\n",
        "  # timed and fewest moves events -> lower z score is better -> flip z score\n",
        "  # multiblind event -> encoded score -> lower z score is better\n",
        "  dplyr::mutate(std_performance = -(avg_performance - mean(avg_performance)) / sd(avg_performance)) |>\n",
        "  dplyr::ungroup() |>\n",
        "  dplyr::select(personId, eventId, std_performance)\n"
      ],
      "metadata": {
